namespace Lof\Rma\Model\Config\Source\Rule;

class Event implements \Magento\Framework\Option\ArrayInterface
{
    /**
     * @return array
     */
    public function toArray()
    {
        return [
            'rma_created'        => __('New RMA has been created'),
            'rma_updated'        => __('RMA has been changed'),
            'new_customer_reply' => __('New reply from customer'),
            'new_staff_reply'    => __('New reply from staff'),
        ];
    }

    /**
     * @return array
     */
    public function toOptionArray()
    {
        $result = [];
        foreach ($this->toArray() as $k => $v) {
            $result[] = ['value' => $k, 'label' => $v];
        }

        return $result;
    }

    /************************/
}
