namespace Lof\Rma\Model\ResourceModel;

class Attachment extends \Magento\Framework\Model\ResourceModel\Db\AbstractDb
{
    /**
     * {@inheritdoc}
     */
    protected function _construct()
    {
        $this->_init('lof_rma_attachment', 'attachment_id');
    }

    /**
     * {@inheritdoc}
     */
    protected function _beforeSave(\Magento\Framework\Model\AbstractModel $object)
    {
        /** @var  \Lof\Rma\Model\Rma $object */
        if (!$object->getId()) {
            $uid = md5("lofrma".time().rand());
            $object->setUid($uid);
        }
        return parent::_beforeSave($object);
    }
}
